import { Component, Input, OnInit } from '@angular/core';
//import { Component, OnInit } from '@angular/core';
import { Item } from 'src/app/models/item.model';
import { ItemService } from 'src/app/services/item.service';
import { ActivatedRoute, Router } from '@angular/router';

@Component({
  selector: 'app-items-list',
  templateUrl: './items-list.component.html',
  styleUrls: ['./items-list.component.css']
})
export class ItemsListComponent implements OnInit {

  items?: Item[];
  currentItem: Item = {};
  currentIndex = -1;
  name = '';
  message = '';

  @Input() viewMode = false;

  @Input() currentItem1: Item = {
    name: '',
    description: ''
  };


  /*constructor(private itemService: ItemService) { }

  ngOnInit(): void {
    this.retrieveItems();
  }*/

  constructor(
    private itemService: ItemService,
    private route: ActivatedRoute,
    private router: Router) { }

  ngOnInit(): void {
    if (!this.viewMode) {
      this.message = '';
      this.getIdItem(this.route.snapshot.params["id"]);
    }
  }


  retrieveItems(): void {
    this.itemService.getAllItems()
      .subscribe({
        next: (data) => {
          this.items = data;
          console.log(data);
        },
        error: (e) => console.error(e)
      });
  }

  refreshList(): void {
    this.retrieveItems();
    this.currentItem = {};
    this.currentIndex = -1;
  }

  setActiveItem(item: Item, index: number): void {
    this.currentItem = item;
    this.currentIndex = index;
  }

  removeAllItems(): void {
    this.itemService.deleteAll()
      .subscribe({
        next: (res) => {
          console.log(res);
          this.refreshList();
        },
        error: (e) => console.error(e)
      });
  }

  searchName(): void {
    this.currentItem = {};
    this.currentIndex = -1;

    this.itemService.findByName(this.name)
      .subscribe({
        next: (data) => {
          this.items = data;
          console.log(data);
        },
        error: (e) => console.error(e)
      });
  }

  getIdItem(id: string): void {
    this.itemService.get(id)
      .subscribe({
        next: (data) => {
          this.currentItem1 = data;
          console.log(data);
        },
        error: (e) => console.error(e)
      });
  }

  updateItem(): void {
    this.message = '';

    this.itemService.update(this.currentItem1.id, this.currentItem1)
      .subscribe({
        next: (res) => {
          console.log(res);
          this.message = res.message ? res.message : 'This item was updated successfully!';
        },
        error: (e) => console.error(e)
      });
  }

  deleteItem(): void {
    this.itemService.delete(this.currentItem1.id)
      .subscribe({
        next: (res) => {
          console.log(res);
          this.router.navigate(['/v1']);
        },
        error: (e) => console.error(e)
      });
  }

}
